import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router";
import Home from "./pages/Home";
import Projects from "./pages/Projects";
import Photos from "./pages/Photos";
import AdminLogin from "./pages/Admin/Login";

import { IoMoonOutline } from "react-icons/io5";
import { MdOutlineWbSunny } from "react-icons/md";

function App() {
  const [dark, setDark] = useState(false);

  const darkModeHandler = () => {
      setDark(!dark);
      document.body.classList.toggle("dark");
      document.documentElement.classList.toggle('bg-[#111828]');
      localStorage.setItem("dark", !dark);
  }

  // localstorage dark mode 
  useEffect(() => {
    const isDark = localStorage.getItem("dark");
    if (isDark==="true") {
      setDark(true);
      document.body.classList.add("dark");
      document.documentElement.classList.add('bg-[#111828]');
    } else {
      setDark(false);
      document.body.classList.remove("dark");
      document.documentElement.classList.remove('bg-[#111828]');
    }
  }, []);

  return (
    <div className="min-h-screen bg-white dark:bg-[#111828] mx-auto font-inter">
      <BrowserRouter>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/projects" element={<Projects />} />
          <Route path="/photos" element={<Photos />} />
          <Route path="/admin" element={<AdminLogin />} />
        </Routes>
      </BrowserRouter>
      <button className="fixed left-5 bottom-5 text-white dark:text-[#111828] p-3 rounded bg-[#111828] dark:bg-white text-sm" onClick={darkModeHandler}>
        {dark ? <MdOutlineWbSunny /> : <IoMoonOutline />}
      </button>
    </div>
  );
}

export default App;
